import React, { Fragment, useEffect, useState } from "react";
import Table from "react-bootstrap/Table";
// import { json } from "react-router-dom";

import EditList from "./editlist";

const List = () => {

    const [calorie_intake, setCalorie_intake] = useState([]);

    // delete function

    const deleteTodo = async () => {
        try {
            const deleteCalorie_intakes = await fetch(`http://localhost:5000/Dashboard/${id}`, {
                method: "DELETE"
            });

            setCalorie_intakes(calorie_intakes.filter(calorie_intake => calorie_intake.id !== id))
        } catch (err) {
            console.error(err.message)
        }
    };

    const getCalorie_intakes = async () => {
        try {

            const response = await fetch("http://localhost:5000/Dashboard")
            const jsonData = await response.json();

            setCalorie_intakes(jsonData)
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getCalorie_intakes();
    });
    
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Calories</th>
                    <th>Protein</th>
                    <th>Carbs</th>
                    <th>Fat</th>
                    <th>actions</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>Chicken Parmesan</td>
                    <td>800cal</td>
                    <td>36g</td>
                    <td>40g</td>
                    <td>10g</td>
                    <td><button>Edit</button> <button>Delete</button></td>
                </tr>
                {todos.map(db => (
                    <tr key={db.id}>
                        <td>{db.foodname}</td>
                        <td>{db.calories}</td>
                        <td>{db.protein}</td>
                        <td>{db.carbs}</td>
                        <td>{db.fat}</td>
                        <td><EditList todo={todo}/> <button onClick={() => deleteCalorie_intakes(calorie_intakes.id)}>Delete</button></td>
                    </tr>
                ))}
            </tbody>
        </Table>
    )
};

export default List;
