import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Table from "react-bootstrap/Table";

export default function AddMacro() {

  const [intake, setIntake] = useState({
    name: "",
    calories: "",
    fat: "",
    protein: "",
    carbohydrates: ""
  });

  const handleChange = e => {
    const value = e.target.value
    setIntake({
      ...intake,
      [e.target.name]: value
    });
  };

  const handleSubmit = e => {
    e.preventDefault();
    console.log(intake);
  };

    return (
//       <form onSubmit={handleSubmit}>
//   <label>
//     Name:
//     <input type="text" name="name" value={intake.name} onChange={handleChange}/>
//   </label>
//   <label>
//     calories:
//     <input type="number" name="calories" value={intake.calories} onChange={handleChange}/>
//   </label>
//   <input type="submit" value="Submit" />
// </form>

     <Form onSubmit={handleSubmit}>
      <Table>
        <thead>
                <tr>
                    <th>Name</th>
                    <th>Calories</th>
                    <th>Protein</th>
                    <th>Carbs</th>
                    <th>Fat</th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
              <tr>
                
                  <td>
                <Form.Group className="mb-3" controlId="name">
        <Form.Control type="text" onChange={handleChange}/>
      </Form.Group></td>
      <td>
                <Form.Group className="mb-3" controlId="calories">
        <Form.Control type="number" onChange={handleChange}/>
      </Form.Group></td>
      <td>
                <Form.Group className="mb-3" controlId="protein">
        <Form.Control type="number" value={intake.protein} onChange={handleChange}/>
      </Form.Group></td>
      <td>
                <Form.Group className="mb-3" controlId="carbohydrates">
        <Form.Control type="number" onChange={handleChange}/>
      </Form.Group></td>
      <td>
                <Form.Group className="mb-3" controlId="fat">
        <Form.Control type="number" onChange={handleChange}/>
      </Form.Group></td>
      <td><Button variant="primary" type="submit">
        Submit
      </Button></td>
              </tr>
            </tbody>
    </Table>
    </Form>
    )
}
