import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Table from "react-bootstrap/Table";

export default function AddMacro() {

  const [intake, setIntake] = useState({
    food: "",
    calories: "",
    fat: "",
    protein: "",
    carbohydrates: ""
  });

  const handleChange = e => {
    const value = e.target.value
    setIntake({
      ...intake,
      [e.target.name]: value
    });
  };

  const onSubmitForm = async e => {
    e.preventDefault();
    try {
      const body = { intake };
      const response = await fetch("http://localhost:5000/diary", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body)
      });

      window.location = "/";
    } catch (err) {
      console.error(err.message);
    }
  };

    return (
     <Form onSubmit={onSubmitForm}>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Calories</th>
            <th>Protein</th>
            <th>Carbs</th>
            <th>Fat</th>
            <th></th>
          </tr>
        </thead>
          <tbody>
            <tr>
              <td>
                <Form.Group className="mb-3" controlId="name">Name: 
                  <Form.Control name="name" type="text" value={intake.name} onChange={handleChange}/></Form.Group></td>
              <td>
                <Form.Group className="mb-3" controlId="calories">Calories: 
                  <Form.Control name="calories" type="number" value={intake.calories} onChange={handleChange}/></Form.Group></td>
              <td>
                <Form.Group className="mb-3" controlId="protein">Protein 
                  <Form.Control name="protein" type="number" value={intake.protein} onChange={handleChange}/></Form.Group></td>
              <td>
                <Form.Group className="mb-3" controlId="fat">Fat: 
                  <Form.Control name="fat" type="number" value={intake.fat} onChange={handleChange}/></Form.Group></td>
              <td>
                <Form.Group className="mb-3" controlId="carbohydrates">Carbs:
                  <Form.Control name="carbohydrates" type="number" value={intake.carbohydrates} onChange={handleChange}/></Form.Group></td>
              <td><Button className="mb-3" variant="primary" type="submit">Submit</Button></td>
              </tr>
            </tbody>
    </Table>
    </Form>
    )
}
