import React, { Fragment, useEffect, useState } from "react";
import Table from "react-bootstrap/Table";
import { json } from "react-router-dom";

const List = () => {

    const [todos, setTodos] = useState([]);

    // delete function

    const deleteTodo = async () => {
        try {
            const deleteTodo = await fetch('http://localhost:3000/List/${id}', {
                method: "DELETE"
            });

            setTodos(todos.filter(todo => todo.todo_id !== id))
        } catch (err) {
            console.error(err.message)
        }
    };

    const getTodos = async () => {
        try {

            const response = await fetch("http://localhost:3000/List")
            const jsonData = await response.json();

            setTodos(jsonData)
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getTodos();
    });
    
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Calories</th>
                    <th>Protein</th>
                    <th>Carbs</th>
                    <th>Fat</th>
                    <th>actions</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>Chicken Parmesan</td>
                    <td>800cal</td>
                    <td>36g</td>
                    <td>40g</td>
                    <td>10g</td>
                    <td><button>Edit</button> <button>Delete</button></td>
                </tr>
                {todos.map(db => (
                    <tr key={db.id}>
                        <td>{db.foodname}</td>
                        <td>{db.calories}</td>
                        <td>{db.protein}</td>
                        <td>{db.carbs}</td>
                        <td>{db.fat}</td>
                        <td><EditList /> <button onClick={() => deleteTodo(todo.todo_id)}>Delete</button></td>
                    </tr>
                ))}
            </tbody>
        </Table>
    )
};

export default List;
